INSERT INTO `eku122x_co_content` VALUES ('33', '1', '1', 'http://www.bdzy.cc/detail/?18312.html', '止杀令', 'array (\n  \\\'title\\\' => \\\'止杀令\\\',\n  \\\'cname\\\' => \\\'动作片\\\',\n  \\\'intro\\\' => \\\'BD\\\',\n  \\\'time\\\' => \\\'2013-3-25 13:17:48\\\',\n  \\\'actor\\\' => \\\'赵有亮 涂们 李小冉 耿乐 余少群\\\',\n  \\\'content\\\' => \\\'电影《止杀令》取材于&ldquo;一言止杀&rdquo;。公元13世纪初，蒙古铁骑横扫欧亚，成吉思汗以求长生之名在中亚见到了昆嵛真人丘处机，闪烁着中国传统文明精华的道教文化思想随之传播到了中世纪的欧亚大陆。不过,后世盛赞丘处机拯救生灵的功德甚至超越他在宗教上的贡献。乾隆皇帝就曾为丘处机写过对联，其中下联是：一言止杀，始知济世有奇功。\\\',\n  \\\'picurl\\\' => \\\'http://pic4.8888fy.com/pic/uploadimg/2013-3/201332213571458843.jpg\\\',\n  \\\'area\\\' => \\\'大陆\\\',\n  \\\'language\\\' => \\\'国语\\\',\n  \\\'year\\\' => \\\'2013\\\',\n  \\\'serial\\\' => \\\'完结\\\',\n  \\\'playurl\\\' => \n  array (\n    0 => \\\'\\\',\n    1 => \\\'bdhd://1043390948|3BCA5EA67A52A376EEC5FBB2C5BDC511|止杀令.BD高清国语中字.rmvb\\\',\n  ),\n  \\\'playlists\\\' => \\\'qvod\\\',\n)', '1364208665');
