INSERT INTO `eku122x_co_content` VALUES ('35', '1', '1', 'http://www.bdzy.cc/detail/?18002.html', '灵异怪谈国语第一季', 'array (\n  \\\'title\\\' => \\\'灵异怪谈国语第一季\\\',\n  \\\'cname\\\' => \\\'海外剧\\\',\n  \\\'intro\\\' => \\\'06集\\\',\n  \\\'time\\\' => \\\'2013-3-25 13:05:54\\\',\n  \\\'actor\\\' => \\\'康妮·布里登 迪伦·麦克德莫特\\\',\n  \\\'content\\\' => \\\'灵异就是我们人类对未知现象的一种统称。某种现象的确发生了，但是以我们现有的科学知识并不能去解释的现象。往往这种现象以一种神秘的方式出现，有时候人们也会把它的神奇程度夸大，使其更难以得到合理的答案。其实任何事物的发生都会有它的道理，只是我们并没有知晓它的科学面。\\\',\n  \\\'picurl\\\' => \\\'http://pic4.8888fy.com/pic/uploadimg/2013-2/20132191244535688.jpg\\\',\n  \\\'area\\\' => \\\'其它\\\',\n  \\\'language\\\' => \\\'国语\\\',\n  \\\'year\\\' => \\\'2013\\\',\n  \\\'serial\\\' => \\\'连载第6集\\\',\n  \\\'playurl\\\' => \n  array (\n    0 => \\\'\\\',\n    1 => \\\'bdhd://78423722|D460BD12D24FAE0E87597439CCA9D919|灵异怪谈国语版01.rmvb\\\',\n    2 => \\\'bdhd://73012266|7E92E1165A43D7D5B7EB49C76FBE8D96|灵异怪谈国语版02.rmvb\\\',\n    3 => \\\'bdhd://100912459|634CF64C74DF211B884024E5DB2FFEFC|灵异怪谈国语版03.rmvb\\\',\n    4 => \\\'bdhd://88304933|1FC6D8B1039E7D626F13BBE1E7F2FCA5|灵异怪谈国语版04.rmvb\\\',\n    5 => \\\'bdhd://81651621|230D1B82E98C35D84FCA62153403D84E|灵异怪谈国语版05.rmvb\\\',\n    6 => \\\'bdhd://145908486|255985C5DEB39AC4B4B42970547D1E82|灵异怪谈国语版06.rmvb\\\',\n  ),\n  \\\'playlists\\\' => \\\'qvod\\\',\n)', '1364208659');
