INSERT INTO `eku122x_co_content` VALUES ('47', '1', '1', 'http://www.bdzy.cc/detail/?18330.html', '生死较量', 'array (\n  \\\'title\\\' => \\\'生死较量\\\',\n  \\\'cname\\\' => \\\'动作片\\\',\n  \\\'intro\\\' => \\\'BD\\\',\n  \\\'time\\\' => \\\'2013-3-25 11:31:17\\\',\n  \\\'actor\\\' => \\\'阿留·阿琼 伊莱亚娜·狄克鲁兹\\\',\n  \\\'content\\\' => \\\'无生死较量电影叙述拉维(Allu Arjun)是一个聪明的大男孩，他不相信勤能致富这个概念。因为他是非常聪明的，他希望钱来的越快越好，拉维不惜冒险得到它。毕图(Sonu Sood)是个从银行抢劫了15亿卢比的匪徒。拉维和毕图在一个夜晚狭路相逢。故事接下来讲述了两个家伙之间的决斗，以及拉维是如何意识到努力工作的重要性，最后成长起来。\\\',\n  \\\'picurl\\\' => \\\'http://pic4.8888fy.com/pic/uploadimg/2013-3/201332511293975066.jpg\\\',\n  \\\'area\\\' => \\\'其它\\\',\n  \\\'language\\\' => \\\'印第语\\\',\n  \\\'year\\\' => \\\'2012\\\',\n  \\\'serial\\\' => \\\'完结\\\',\n  \\\'playurl\\\' => \n  array (\n    0 => \\\'\\\',\n    1 => \\\'bdhd://1963322111|CCC6935F49F8938D9F22D01DA2090305|生死较量BD1280高清中字.mkv\\\',\n  ),\n  \\\'playlists\\\' => \\\'qvod\\\',\n)', '1364208623');
