INSERT INTO `eku122x_co_content` VALUES ('49', '1', '1', 'http://www.bdzy.cc/detail/?18327.html', '宫中残酷史-花朵们的战争', 'array (\n  \\\'title\\\' => \\\'宫中残酷史-花朵们的战争\\\',\n  \\\'cname\\\' => \\\'韩国剧\\\',\n  \\\'intro\\\' => \\\'02集\\\',\n  \\\'time\\\' => \\\'2013-3-25 11:26:28\\\',\n  \\\'actor\\\' => \\\'金贤珠 李德华 郑成云 金珠英\\\',\n  \\\'content\\\' => \\\'《宫中残酷史-花朵们的战争/宫中残酷史-花的战争》以朝鲜古代为时代背景，讲述了残酷的封建制度下女性的苦难史，并以全方位地描绘提供了一幅古代韩国社会的风情画。影片女主人公吉礼的一生大致分为四个历史阶段：没落官家的小姐、年轻寡妇、官宦之家的下女、官家媳妇。但无论那个身份，悲惨的命运是始终如一的。封建社会对吉礼的残酷压榨集中表现在对她肉体的摧残上。在传统封建社会，儒教思想将&ldquo;贞节&rdquo;的大帽子扣在女子的头上，令其没有翻身余地，她们的灵与肉被彻底受控，当她们脱离这种控制时又会遭遇死亡的威胁。吉礼在性方面的自由主要体现在充当社会最底层的长工的妻子时期，当她重新进入官家时肉体再次受到摧残，甚至被曾经挚爱的丈夫亲手杀死。《花的战争》用一个缜密的故事和强烈的色彩冲击营造了女主人公凄艳的一生，并控诉了压迫女子精神与肉体双向自由的儒教糟粕思想。影片的批判精神在今天看来仍是可圈可点，具有很强的指导意义。\\\',\n  \\\'picurl\\\' => \\\'http://pic4.8888fy.com/pic/uploadimg/2013-3/201332423515044227.jpg\\\',\n  \\\'area\\\' => \\\'韩国\\\',\n  \\\'language\\\' => \\\'韩语\\\',\n  \\\'year\\\' => \\\'2013\\\',\n  \\\'serial\\\' => \\\'连载第2集\\\',\n  \\\'playurl\\\' => \n  array (\n    0 => \\\'\\\',\n    1 => \\\'bdhd://709287680|A423E930C279147A55EC1D83C83FE456|宫中残酷史-花朵们的战争01.rmvb\\\',\n    2 => \\\'bdhd://537157063|4579F26B176BDCBA8BF6325DDFBFE396|宫中残酷史[第2集].rmvb\\\',\n  ),\n  \\\'playlists\\\' => \\\'qvod\\\',\n)', '1364208617');
INSERT INTO `eku122x_co_content` VALUES ('50', '1', '0', 'http://www.bdzy.cc/detail/?17439.html', '劲歌金曲2013', 'array (\n  \\\'title\\\' => \\\'劲歌金曲2013\\\',\n  \\\'cname\\\' => \\\'综艺\\\',\n  \\\'intro\\\' => \\\'20130323\\\',\n  \\\'time\\\' => \\\'2013-3-25 11:23:41\\\',\n  \\\'actor\\\' => \\\'众明星\\\',\n  \\\'content\\\' => \\\'《劲歌金曲》(Jade Solid Gold)是由香港TVB制作的专业流行音乐节目，时至今天成为全球最长寿的华语音乐节目。于1981年10月10日播出第一集，现在仍然是香港最具影响力的音乐节目，目前播出时间为每周五晚19:05在TVB翡翠台及高清翡翠台首播，而高清翡翠台则会在周日晚18:00播出足本1小时版本。另有古巨基同名歌曲《劲歌金曲》。\\\',\n  \\\'picurl\\\' => \\\'http://pic4.8888fy.com/pic/uploadimg/2013-1/20131717534410315.jpg\\\',\n  \\\'area\\\' => \\\'香港\\\',\n  \\\'language\\\' => \\\'国语\\\',\n  \\\'year\\\' => \\\'2013\\\',\n  \\\'serial\\\' => \\\'连载第20130323集\\\',\n  \\\'playurl\\\' => \n  array (\n    0 => \\\'\\\',\n    1 => \\\'bdhd://115756106|BC39C14B305A329EF2A5994820C977BE|勁歌金曲20130105.rmvb\\\',\n    2 => \\\'bdhd://113806166|CB9443778490A5506B8D1DEDAC58C0E7|劲歌金曲20130119.rmvb\\\',\n    3 => \\\'bdhd://142967243|262445337BA60E203D872504B233647C|勁歌金曲20130126.rmvb\\\',\n    4 => \\\'bdhd://142990412|924D317ED4FFF9C0E0B8CF928FD7317F|劲歌金曲_20130202.rmvb\\\',\n    5 => \\\'bdhd://149679621|F842745E2DD0440669ECC9EF4BC2856D|劲歌金曲20130216.rmvb\\\',\n    6 => \\\'bdhd://183668108|147C4FBCFB187878FC1BA687ED78A385|劲歌金曲20130223.rmvb\\\',\n    7 => \\\'bdhd://146712584|93C47D7E39E5B42C4425C222DC02F33D|劲歌金曲20130302.rmvb\\\',\n    8 => \\\'bdhd://156307708|5F3E822824AB5AF5F83936EFF9E1F101|劲歌金曲20130309.rmvb\\\',\n    9 => \\\'bdhd://155129622|C42C8F4B00D5D71C91BB9B8FF5B704ED|劲歌金曲20130316.rmvb\\\',\n    10 => \\\'bdhd://115121549|3F2EE9BABD15CC5B56550A43178945CC|劲歌金曲20130323.rmvb\\\',\n  ),\n  \\\'playlists\\\' => \\\'qvod\\\',\n)', '1364208607');
